const fs = require\('fs'\);
const content = fs.readFileSync\('/Users/selik/Documents/GitHub/BBD-evolution-code-clone/telegram-archive/course/build-all.js', 'utf-8'\);

// Find the level 5 section
const level5Start = content.indexOf\('num: 5, emoji:'\);
const level5End = content.indexOf\('// ===', level5Start + 100\);
const level5Content = content.substring\(level5Start, level5End\);

// Extract topics specifically from level 5
const topicRegex = /slug: '\([^']+\)',\\s*title: \\{\\s*en: '\([^']*\(?:\\\\'[^']*\)*\)',\\s*uk: '\([^']*\(?:\\\\'[^']*\)*\)'\\s*\\},\\s*desc: \\{\\s*en: '\([^']*\(?:\\\\'[^']*\)*\)',\\s*uk: '\([^']*\(?:\\\\'[^']*\)*\)'/g;

let match;
const level5Topics = [];

while \(\(match = topicRegex.exec\(level5Content\)\) !== null\) {
  level5Topics.push\({
    slug: match[1],
    titleEN: match[2].replace\(/\\\\'/g, "'"\),
    titleUK: match[3].replace\(/\\\\'/g, "'"\),
    descEN: match[4].replace\(/\\\\'/g, "'"\),
    descUK: match[5].replace\(/\\\\'/g, "'"\)
  }\);
}

console.log\('LEVEL 5 - All Topics:\\n'\);
level5Topics.forEach\(\(topic, idx\) => {
  console.log\(`${idx + 1}. slug: ${topic.slug}`\);
  console.log\(`   EN: ${topic.titleEN}`\);
  console.log\(`   UK: ${topic.titleUK}`\);
  console.log\(`   descEN: ${topic.descEN}`\);
  console.log\(`   descUK: ${topic.descUK}`\);
  console.log\(''\);
}\);

console.log\(`\\nTotal Level 5 topics: ${level5Topics.length}`\);
EOF)
